function initannuaire(){var e=$("#name").val();$.ajax({url:"http://udamobile.u-clermont1.fr/v2/annuaire/searchpage.php",type:"POST",data:{name:e},cache:true,success:function(e){makelist(e)}})}function makelist(e){var t="";if(e!=""){var n=jQuery.isPlainObject(e)?e:jQuery.parseJSON(e);var r=n.count;if(r==0){t+="<li class='ElementListe'><p>Pas resultat pour la recherche demand√©</p></li>"}else{var s=n.exact;for(i=0;i<r;i++){t+='<li class="annuaire" data-corners="false" data-wrapperels="div"><div class="ui-btn-inner ui-li"><div class="ui-btn-text"><h3>'+s[i].nom+" "+s[i].prenom+'</h3><img src="css/mail_green.png">Courriel:'+'<p><a class="udamob_contact_a" href=\'mailto:'+s[i].mail+"'>"+s[i].mail+"</a></p>";if(s[i].tel){t+='<img src="css/phone.png">TEL:'+'<a class="udamob_contact_a" href=\'tel:'+s[i].tel+"'>"+s[i].tel+"</a>"}t+="</div></div></li>"}}$("#listAnnu").html(t)}}