function init(){var e=$("#username").val();var t=$("#password").val();if(e==""||t==""){}else{$.ajax({url:"http://udamobile.u-clermont1.fr/v2/connexion/",type:"POST",data:{username:e,password:t},cache:true,dataType:"json",success:function(e){reponse=jQuery.isPlainObject(e)?e:jQuery.parseJSON(e);window.localStorage.setItem("connecte","oui");window.localStorage.setItem("uid",reponse.uid);window.localStorage.setItem("password",t);window.localStorage.setItem("display",reponse.display);window.localStorage.setItem("nom",reponse.nom);window.localStorage.setItem("prenom",reponse.prenom);window.localStorage.setItem("mail",reponse.mail);window.localStorage.setItem("affil",reponse.affil);if(reponse.affil=="student"){window.localStorage.setItem("codetu",reponse.codetu);window.localStorage.setItem("ine",reponse.ine)}$("#username").val(window.localStorage.getItem("uid"));$("#password").val(window.localStorage.getItem("password"));if(reponse.affil=="student")window.location="./main_student.html";else window.location="./main_member.html"},error:function(){$("#err").html("Erreur, vérifiez les données saisies")}})}}document.addEventListener("deviceready",onDeviceReady,false)